enum INDICES {
  COUNTER_CLOCKWISE = 0,
  CLOCKWISE,
  ONLINE_BACK,
  ON_SEGMENT,
  ONLINE_FRONT
};

const std::vector<std::string> strLst = {
  "COUNTER_CLOCKWISE",
  "CLOCKWISE",
  "ONLINE_BACK",
  "ON_SEGMENT",
  "ONLINE_FRONT"
};

const double tol = 1e-16;

int main() {
  double x0, y0, x1, y1;
  std::cin >> x0 >> y0 >> x1 >> y1;

  int q;
  std::cin >> q;

  for (int i = 0; i < q; ++i) {
    double x2, y2;
    std::cin >> x2 >> y2;

    int index;
    if ((x2 == x0 && y2 == y0) || (x2 == x1 && y2 == y1)) {
      index = INDICES::ON_SEGMENT;
    } else {
      double ip = (x1 - x0) * (x2 - x0) + (y1 - y0) * (y2 - y0);
      double norm1 = std::sqrt((x1 - x0) * (x1 - x0) + (y1 - y0) * (y1 - y0));
      double norm2 = std::sqrt((x2 - x0) * (x2 - x0) + (y2 - y0) * (y2 - y0));
      double theta = ip / norm1 / norm2;
      if (theta < -1 + tol) {
        index = INDICES::ONLINE_BACK;
      } else if (theta > 1 - tol) {
        index = (norm1 < norm2) ? INDICES::ONLINE_FRONT : INDICES::ON_SEGMENT;
      } else {
        double opz = (x1 - x0) * (y2 - y0) - (x2 - x0) * (y1 - y0);
        index = (opz > 0) ? INDICES::COUNTER_CLOCKWISE : INDICES::CLOCKWISE;
      }
    }

    std::cout << strLst[index] << std::endl;
  }

  return 0;
}

