enum CCW{LEFT=1, RIGHT=2, BACK=4, FRONT=8, ON=16};
int ccw(P a, P b, P c) {
	P p = div(c-a, b-a);
	if(imag(p) > 0) return LEFT;
	if(imag(p) < 0) return RIGHT;
	if(real(p) < 0) return BACK;
	if(real(p) > 1) return FRONT;
	return ON;
}

