        cin >> p.x >> p.y;

        switch(ccw(p0, p1, p)) {
            case 1: cout << "COUNTER_CLOCKWISE"; break;
            case -1: cout << "CLOCKWISE"; break;
            case 2: cout << "ONLINE_BACK"; break;
            case -2: cout << "ONLINE_FRONT"; break;
            case 0: cout << "ON_SEGMENT"; break;
        }
        cout << endl;
    }

    return 0;
}

