Point projection(const Segment &l, const Point &p){
    double t = dot(p - l.first, l.second - l.first) / norm(l.second - l.first);
    return l.first + t * (l.second - l.first);
}
/*
直線 l に関して線対称な点を求める
*/
Point reflection(const Segment &l, const Point &p){
    return p + (projection(l,p)-p)*2.0;
}
/*
crockwise
1: 時計回り
2: 反時計回り
一直線
+2: a -> b -> c
-2: c -> a -> b
0: a -> c -> c
*/
int ccw(const Point &a, const Point &b, const Point &c){
    Point x = b - a;
    Point y = c - a;
    if(cross(x,y) > EPS)return 1;
    if(cross(x,y) < -EPS)return -1;
    if(dot(x,y) < -EPS)return +2;
    if(norm(x) < norm(y))return -2;
    return 0;
}
