		if(ans==1) printf("CLOCKWISE");
		if(ans==2) printf("ONLINE_BACK");
		if(ans==3) printf("ONLINE_FRONT");
		if(ans==4) printf("ON_SEGMENT");
		printf("\n");
	}
}

