/*二点の角度*/
/*0~2π*/
double angle_line_0_359(double x1, double y1, double x2, double y2){
    double angle = atan2(y2-y1, x2-x1);
    if(angle<0)angle = 2*M_PI+angle;
    return angle;
}

//-------------------------------------------------------------------------------------
/*二辺の角度*/
/*反時計回りの内側*/
/*0~2π*/
double angle_two_line_0_359(double x1, double y1, double x2, double y2, double x3, double y3){
    double angle = atan2(y2-y1, x2-x1);
    if(angle<0)angle = 2*M_PI+angle;
    if(angle==2*M_PI)angle=0.0;

    double angle2 = atan2(y3-y2, x3-x2);
    if(angle2<0)angle2 = 2*M_PI+angle2;
    if(angle2==2*M_PI)angle2=0.0;

    return 2*M_PI-( angle+(M_PI-angle2) );
}

//-------------------------------------------------------------------------------------
/*二次元配列のクイックソート*/
/*xoryが0ならx座標　1なら座標でソート*/
void two_dimensional_qsort(double xy[100000][2], int left, int right, int xory)
{
    int i, j;
    double pivot,pivot2;

    i = left;                      /* ソートする配列の一番小さい要素の添字 */
    j = right;                     /* ソートする配列の一番大きい要素の添字 */

    pivot = xy[(left + right) / 2][xory]; /* 基準値を配列の中央付近にとる */
    pivot2 = xy[(left + right) / 2][(xory+1)%2];


    while (1) {                    /* 無限ループ */

        while (xy[i][xory] < pivot || (xy[i][xory] == pivot && xy[i][(xory+1)%2] < pivot2))       /* pivot より大きい値が */
            i++;                   /* 出るまで i を増加させる */

        while (pivot < xy[j][xory] || (xy[j][xory] == pivot &&  pivot2 < xy[j][(xory+1)%2]))       /* pivot より小さい値が */
            j--;                   /*  出るまで j を減少させる */
        if (i >= j)                /* i >= j なら */
            break;                 /* 無限ループから抜ける */

        swap(xy[i][xory], xy[j][xory]);            /* x[i] と x[j]を交換 */
        swap(xy[i][(xory+1)%2], xy[j][(xory+1)%2]);            /* x[i] と x[j]を交換 */
        i++;                       /* 次のデータ */
        j--;
    }

    if (left < i - 1)              /* 基準値の左に 2 以上要素があれば */
        two_dimensional_qsort(xy, left, i - 1, xory);     /* 左の配列を Q ソートする */
    if (j + 1 <  right)            /* 基準値の右に 2 以上要素があれば */
        two_dimensional_qsort(xy, j + 1, right, xory);    /* 右の配列を Q ソートする */
}


//-------------------------------------------------------------------------------------
/*凸包*/
/*２次元平面における点の集合Pに対する凸包（convex hull）を求めて下さい。凸包とは点集合Pの全ての点を含む最小の凸多角形です。*/
/*集合の要素数 n*/
/*集合 p_xy[1000000][2]*/
/*解答の要素数 q*/
/*解答 ans[1000000][2]　最も下の最も左から反時計回り*/

void convex_hull(int n, double p_xy[100000][2], int *q, double ans[100000][2]){

    two_dimensional_qsort(p_xy, 0, n-1, 1);
    /*
    for(int i=0; i<n; i++){
        cout<<p_xy[i][0]<<" "<<p_xy[i][1]<<endl;
    }
     */


    int k=0;
    for(int i=0; i<n; i++){
        while(k > 1 && (angle_two_line_0_359(ans[k-2][0], ans[k-2][1], ans[k-1][0], ans[k-1][1], p_xy[i][0], p_xy[i][1])>M_PI))k--;
        ans[k][0]=p_xy[i][0];
        ans[k][1]=p_xy[i][1];
        k++;
    }


    int t=k;
    for(int i=n-2; i>=0; i--){
        while(k > t && (angle_two_line_0_359(ans[k-2][0], ans[k-2][1], ans[k-1][0], ans[k-1][1], p_xy[i][0], p_xy[i][1])>M_PI))k--;
        ans[k][0]=p_xy[i][0];
        ans[k][1]=p_xy[i][1];
        k++;
    }
    *q=k-1;
}
