/*点の集合の最近点距離*/
/*使用前に sort(a, a+n); をおこなう*/
/*使用注意 他と仕様が違う*/
/*点にpairを使っている*/


bool compare_y(pair<double, double> a,pair<double, double> b){
    return a.second<b.second;
}

double length_of_the_nearest_point_to(pair<double, double> a[100000], int n){
    if(n<=1)return 999999999.9;
    int m=n/2;
    double x=a[m].first;
    double d=min(length_of_the_nearest_point_to(a, m), length_of_the_nearest_point_to(a+m, n-m));
    //inplace_merge(a, a+m, a+n, compare_y);
    sort(a, a+n, compare_y);
    vector<pair<double, double> >b;
    for(int i=0; i<n; i++){
        if(fabs(a[i].first-x)>=d)continue;
        for(int j=0; j<b.size(); j++){
            double dx=a[i].first-b[b.size()-j-1].first;
            double dy=a[i].second-b[b.size()-j-1].second;
            if(dy>=d)break;
            d=min(d,sqrt(dx*dx+dy*dy));

        }
        b.push_back(a[i]);
    }
    return d;
}

