Point pointprojection(Point a, Point b, Point c)
{
    Point v1 = b - a;
    Point v2 = c - a;
    Point d = v1 * (v1.dot(v2) / (v1.norm() * v1.norm())) + a;
    return d;
}

Point pointreflection(Point a, Point b, Point c)
{
    Point d = pointprojection(a, b, c);
    Point e = 2 * d - c;
    return e;
}

void pointargument(Point a, Point b, Point c)
{
    Point vba = b - a;
    Point vca = c - a;
    double s = vba * vca;
    if (s > 0)
        cout << "COUNTER_CLOCKWISE" << endl;
    else if (s < 0)
        cout << "CLOCKWISE" << endl;
    else
    {
        double p = vca / vba;
        if (p < 0)
            cout << "ONLINE_BACK" << endl;
        else if (0 <= p && p <= 1)
