            case -1: {cout << "CLOCKWISE" << endl; continue; }
            case 2: {cout << "ONLINE_BACK" << endl; continue; }
            case -2: {cout << "ONLINE_FRONT" << endl; continue; }
            case 0: {cout << "ON_SEGMENT" << endl; continue; }
        }
    }
}

