using namespace std;

#define EPS (1e-10)

class Point {
public:
	double x, y;
	Point(double x, double y) :
			x(x), y(y) {
	}
	Point operator -(Point p) {
		return Point(x - p.x, y - p.y);
	}

	double norm() {
		return x * x + y * y;
	}
};

double dot(Point p1, Point p2) {
	return p1.x * p2.x + p1.y * p2.y;
}

double cross(Point p1, Point p2) {
	return p1.x * p2.y - p1.y * p2.x;
}

static const int COUNTER_CLOCKWISE = 1;
static const int CLOCKWISE = -1;
static const int ONLINE_BACK = 2;
static const int ON_SEGMENT = -2;
static const int ONLINE_FRONT = 0;

int ccw(Point p0, Point p1, Point p2) {
	Point a = p1 - p0;
	Point b = p2 - p0;
	if (cross(a, b) > EPS)
		return COUNTER_CLOCKWISE;
	if (cross(a, b) < -EPS)
		return CLOCKWISE;
	if (dot(a, b) < -EPS)
		return ONLINE_BACK;
	if (a.norm() < b.norm())
		return ONLINE_FRONT;

	return ON_SEGMENT;
}

int main() {

	int p0x, p0y, p1x, p1y;
	scanf("%d %d %d %d", &p0x, &p0y, &p1x, &p1y);
	Point p0(p0x, p0y);
	Point p1(p1x, p1y);

	int q;
	scanf("%d", &q);
	for (int i = 0; i < q; ++i) {
		int p2x, p2y;
		scanf("%d %d", &p2x, &p2y);
		Point p2(p2x, p2y);

		switch (ccw(p0, p1, p2)) {
		case COUNTER_CLOCKWISE:
			cout << "COUNTER_CLOCKWISE" << endl;
			break;
		case CLOCKWISE:
			cout << "CLOCKWISE" << endl;
			break;
		case ONLINE_BACK:
			cout << "ONLINE_BACK" << endl;
			break;
		case ON_SEGMENT:
			cout << "ON_SEGMENT" << endl;
			break;
		case ONLINE_FRONT:
			cout << "ONLINE_FRONT" << endl;
			break;
		default:
			break;
		}

	}

	return 0;
}

