	for (int i = 0; i < q; i++){
		cin >> x2 >> y2;
		a = Point(x1, y1) - Point(x0, y0);
		b = Point(x2, y2) - Point(x0, y0);
		result = cross(a, b);
		if      (result > EPS)        printf("COUNTER_CLOCKWISE\n" );
		else if (result < EPS)		  printf("CLOCKWISE\n"          );
		else if (result < -EPS)		  printf("ONLINE_BACK\n"       );
		else if (a.norm() < b.norm()) printf("ONLINE_FRONT\n");
	}

	return 0;
}

