// http://judge.u-aizu.ac.jp/onlinejudge/description.jsp?id=CGL_1_A
// http://judge.u-aizu.ac.jp/onlinejudge/review.jsp?rid=2376299#1
P2 projection(P2 p1, P2 p2, P2 p) {
   P2 t = p2 - p1;
   return p1+(t)/norm(t)*real((p-p1)*conj(t));
}
// http://judge.u-aizu.ac.jp/onlinejudge/description.jsp?id=CGL_1_B
// http://judge.u-aizu.ac.jp/onlinejudge/review.jsp?rid=2376317#1
P2 refrection(P2 p1, P2 p2, P2 p) {
   P2 t = p2 - p1;
   return p1+conj((p-p1)/t)*t;
}
P2 cc(P2 p1, P2 p2, P2 p) {
   P2 t = p2 - p1;
   return (p-p1)/t;
}


struct Solver {
   Solver() { fprintf(stderr, "--------Solver begin--------\n"); }
   ~Solver() { fprintf(stderr, "--------Solver end--------\n"); }
   void solve() {
      long x1, y1, x2, y2; scan(x1, y1, x2, y2);
      long q; scan(q);
      for(long i = 0; i < q; ++i) {
         long x, y; scan(x, y);
         auto res = cc(P2(x1,y1), P2(x2, y2), P2(x,y));
         double eps = 1e-10;
         if( res.imag() > eps ) puts("COUNTER_CLOCKWISE");
         else if( res.imag() < -eps ) puts("CLOCKWISE");
         else if( res.real() < -eps ) puts("ONLINE_BACK");
         else if( -eps < res.real() and res.real() < 1.0+eps ) puts("ON_SEGMENT");
         else if( 1.0+eps < res.real() ) puts("ONLINE_FRONT");
         else puts("error");
      }
   }
};

int main() {
  loop std::unique_ptr<Solver>(new Solver())->solve();
}

