int ccw(const Point &a, const Point &b, const Point &c) {
	const Point p = b - a;
	const Point q = c - a;
	const Real cr = cross(p,q);

	if (sign(cr) == 1) { return CCW; }
	else if (sign(cr) == -1) { return CW; }
	else if (sign(dot(p,q)) == -1) { return BACK; }
	else if (sign(norm(p) - norm(q)) == -1) { return FRONT; }
	return ON;
}


