		D x,y;
		scanf("%lf%lf",&x,&y);
		int k=ccw(a,b,P(x,y));
		if(k==CCW) printf("COUNTER_CLOCKWISE\n");
		if(k==CW) printf("CLOCKWISE\n");
		if(k==FRONT) printf("ONLINE_FRONT\n");
		if(k==BACK) printf("ONLINE_BACK\n");
		if(k==ON) printf("ON_SEGMENT\n");

	}
	return 0;
}

