int ccw(Point p0, Point p1, Point p2) {
  Vector a = p1-p0;
  Vector b = p2-p0;
  if (cross(a, b) > EPS) return 1;
  if (cross(a, b) < -EPS) return -1;
  if (dot(a, b) < -EPS) return 2;
  if (a.norm() < b.norm()) return -2;

  return 0;
}

int main() {
  Vector p0, p1;
  cin >> p0.x >> p0.y >> p1.x >> p1.y;
  int q;
  cin >> q;
  for (int i = 0; i < q; i++) {
    Vector p2;
    cin >> p2.x >> p2.y;
    int ccwret = ccw(p0, p1, p2);
    if (ccwret == 1) {
      cout << "COUNTER_CLOCKWISE" << endl;
    }
    if (ccwret == -1) {
      cout << "CLOCKWISE" << endl;
    }
    if (ccwret == 2) {
      cout << "ONLINE_BACK" << endl;
    }
    if (ccwret == -2) {
      cout << "ONLINE_FRONT" << endl;
    }
    if (ccwret == 0) {
      cout << "ON_SEGMENT" << endl;
    }
  }
}

