        if(ans == CLOCKWISE) puts("CLOCKWISE");
        if(ans == ONLINE_BACK) puts("ONLINE_BACK");
        if(ans == ONLINE_FRONT) puts("ONLINE_FRONT");
        if(ans == ON_SEGMENT) puts("ON_SEGMENT");
    }
    return 0;
}

