        if(res == 1)    cout << "COUNTER_CLOCKWISE" << endl;
        if(res == -1)   cout << "CLOCKWISE" << endl;
        if(res == 2)    cout << "ONLINE_BACK" << endl;
        if(res == -2)   cout << "ONLINE_FRONT" << endl;
        if(res == 0)    cout << "ON_SEGMENT" << endl;
    }
    return 0;
}

