inline void solve()
{
    Line l;
    l.read();
    int q;
    cin >> q;
    while (q--)
    {
        Point p;
        p.read();
        int re = l.relation(p);
        if (re == 1)
            cout << "COUNTER_CLOCKWISE\n";
        else if (re == -1)
            cout << "CLOCKWISE\n";
        else if (re == -2)
            cout << "ONLINE_BACK\n";
        else if (re == 2)
            cout << "ONLINE_FRONT\n";
        else
            cout << "ON_SEGMENT\n";
    }
}

