// Counter-Clockwise predicate (a, b, c)
enum CCW {
    COUNTER_CLOCKWISE = 1,      // counter clockwise
    CLOCKWISE         = -1,     // clockwise
    ONLINE_FRONT      = 0,      // a--c--b on line
    ONLINE_BACK       = 2,      // c--a--b on line
    ON_SEGMENT        = -2,     // a--b--c on line
};
int ccw(const Point &a, Point b, Point c) {
    b -= a;  c -= a;
    if (abs_cross(b, c) > 0) return COUNTER_CLOCKWISE;
    if (abs_cross(b, c) < 0) return CLOCKWISE;
    if (dot(b, c) < 0)       return ONLINE_BACK;
    if (b.abs2() < c.abs2())   return ONLINE_FRONT;
    return ON_SEGMENT;
}


/**
 * Line or Segment in two dimensional
 */
class Line : public std::array<Point, 2> {
public:
    Line() {}
    Line(const Point &p1, const Point &p2) {
        (*this)[0] = p1;
        (*this)[1] = p2;
    }
};
