/**
 * Intersection point and Distance
 */
Point Projection(const Line &l, const Point &p) {
    Point dir = l[1] - l[0];
    Number t = dot(p - l[0], dir) / dir.abs2();
    return l[0] + dir * t;
}
Point Reflection(const Line &l, const Point &p) {
    return Projection(l, p) * 2.0 - p;
}


