double eps = 1e-9;

string ccw(P a,P b,P c){
  P ab=b-a;
  P ac=c-a;
  P k=ac/ab;
  if( k.imag() > eps ){
    return "COUNTER_CLOCKWISE";
  }
  if( k.imag() < -eps ){
    return "CLOCKWISE";
  }
  if( k.real() < -eps ){
    return "ONLINE_BACK";
  }
  if( (ac/ac).real()+eps < k.real() ){
    return "ONLINE_FRONT";
  }
  return "ON_SEGMENT";
}

