class Segment {
public:
  Point p1, p2;

  Segment() {}
  Segment(Point p1, Point p2): p1(p1), p2(p2) {}
};

//線分sに対する点pの射影
Point project(Segment s, Point p) {
  Point base = s.p2 - s.p1;
  ld r = dot(p - s.p1, base) / base.norm();
  return s.p1 + base * r;
}

//線分sを対称軸とした点pの線対象の点
Point reflect(Segment s, Point p) {
  return p + (project(s, p) - p) * 2;
}

