int ccw(Point p0, Point p1, Point p2) {
  Point a = p1 - p0;
  Point b = p2 - p0;
  if (cross(a, b) > EPS) return 1;
  if (cross(a, b) < -EPS) return -1;
  if (dot(a, b) < -EPS) return 2;
  if (a.norm() < b.norm()) return -2;
  return 0;
}

//直線lと点pの距離
ld getDistanceLP(Segment l, Point p) {
  return abs(cross(l.p2 - l.p1, p - l.p1) / (l.p2 - l.p1).abs());
}

//線分sと点pの距離
ld getDistanceSP(Segment s, Point p) {
  if (dot(s.p2 - s.p1, p - s.p1) < 0) return (p - s.p1).abs();
  if (dot(s.p1 - s.p2, p - s.p2) < 0) return (p - s.p2).abs();
  return getDistanceLP(s, p);
}



