void vecscl(double c, const double v0[2], double v1[2])
{
    v1[0] = v0[0] * c;
    v1[1] = v0[1] * c;
}

void vecadd(const double v0[2], const double v1[2], double v2[2])
{
    v2[0] = v0[0] + v1[0];
    v2[1] = v0[1] + v1[1];
}

void vecsub(const double v0[2], const double v1[2], double v2[2])
{
    v2[0] = v0[0] - v1[0];
    v2[1] = v0[1] - v1[1];
}

double vecprd(const double v0[2], const double v1[2])
{
    return v0[0] * v1[1] - v0[1] * v1[0];
}

double vecsum(const double v0[2], const double v1[2])
{
    return v0[0] * v1[0] + v0[1] * v1[1];
}

double veclen(const double v[2])
{
    return sqrt(v[0] * v[0] + v[1] * v[1]);
}

bool vecnrm(double eps, const double v0[2], double v1[2])
{
    double len = veclen(v0);
    if (len < eps) {
        return false;
    }
    vecscl(1 / len, v0, v1);
    return true;
}

const double eps = 1e-12;

void solve()
{
    int x0, y0, x1, y1;
    scanf("%d%d%d%d", &x0, &y0, &x1, &y1);
    double p0[2] = { (double)x0, (double)y0 };
    double p1[2] = { (double)x1, (double)y1 };
    double v01[2];
    vecsub(p1, p0, v01);

    int Q;
    scanf("%d", &Q);
    int t;
    for (t = 0; t < Q; t++) {
        int x, y;
        scanf("%d%d", &x, &y);
        double v[2] = { (double)x, (double)y };
        double v02[2];
        vecsub(v, p0, v02);

        double tmp=vecprd(v01, v02);
        if (tmp > eps) {
            printf("COUNTER_CLOCKWISE\n");
        }
        else if (tmp < -eps) {
            printf("CLOCKWISE\n");
        }
        else {
            double tmp2 = vecsum(v01, v02);
            if (tmp2 < -eps) {
                printf("ONLINE_BACK\n");
            }
            else {
                double v12[2];
                vecsub(v, p1, v12);
                double tmp3 = vecsum(v01, v12);
                if (tmp3 > eps) {
                    printf("ONLINE_FRONT\n");
                }
                else {
                    printf("ON_SEGMENT\n");
                }
            }
        }
    }

    return;
}


