//叉?
Type Cross(Vector a,Vector b)
{
    return a.x*b.y-a.y*b.x;
}

//点?
Type Dot(Vector a,Vector b)
{
    return a.x*b.x+a.y*b.y;
}

//?度
Type Length(Vector v)
{
    return sqrt(v.x*v.x+v.y*v.y);
}

int ccw(Point p0,Point p1,Point p2)
{
    Vector a=p1-p0;
    Vector b=p2-p0;
    if(Cross(a,b)>EPS)
        return COUNTER_CLOCKWISE;
    if(Cross(b,a)>EPS)
        return CLOCKWISE;
    if(Dot(a,b)<-EPS)
        return ONLINE_BACK;
    if(Length(a)<Length(b))
        return ONLINE_FRONT;
    if(Length(a)>Length(b))
        return ON_SEGMENT;
}

int main()
{
    Point p0,p1,p2;
    p0.read();
    p1.read();
    int q;
    scanf("%d",&q);
    while(q--)
    {
        p2.read();
        int flag=ccw(p0,p1,p2);
        if(flag==COUNTER_CLOCKWISE)
            puts("COUNTER_CLOCKWISE");
        if(flag==CLOCKWISE)
            puts("CLOCKWISE");
        if(flag==ONLINE_BACK)
            puts("ONLINE_BACK");
        if(flag==ONLINE_FRONT)
            puts("ONLINE_FRONT");
        if(flag==ON_SEGMENT)
            puts("ON_SEGMENT");
    }
    return 0;
}

