	const Point p = b-a;
	const Point q = c-a;
	const Real crs = cross(p,q);

	if(sign(crs) == 1) return CCW;
	if(sign(crs) == -1) return CW;
	if(sign(dot(p,q)) == -1) return BACK;
	if(sign(norm(p) - norm(q)) == -1) return FRONT;
	return ON;
}

