	Segment(double x1 = 0.0, double y1 = 0.0, double x2 = 0.0, double y2 = 0.0):
		p1(x1, y1), p2(x2, y2) {}
	Segment(Point a1, Point a2) : p1(a1), p2(a2) {}
	void insert(Vector a1, Vector a2) { p1 = a1; p2 = a2; }
};
typedef Segment Line;
inline double dot(Vector a, Vector b) { return a.x * b.x + a.y * b.y; }
inline double cross(Vector a, Vector b) { return a.x * b.y - a.y * b.x; }
inline bool isOrthogonal(Vector a, Vector b) { return equals(dot(a, b), 0.0); }
inline bool isOrthogonal(Vector a1, Vector a2, Vector b1, Vector b2) {
	return isOrthogonal(a1 - a2, b1 - b2);
}
inline bool isOrthogonal(Segment s1, Segment s2) {
	return equals(dot(s1.p2 - s1.p1, s2.p2 - s2.p1), 0.0);
}
inline bool isParallel(Vector a, Vector b) { return equals(cross(a, b), 0.0); }
inline bool isParallel(Vector a1, Vector a2, Vector b1, Vector b2) {
	return isParallel(a1 - a2, b1 - b2);
}
inline bool isParallel(Segment s1, Segment s2) {
	return equals(cross(s1.p2 - s1.p1, s2.p2 - s2.p1), 0.0);
}
Point project(Segment s, Point p) {
	Vector base = s.p2 - s.p1;
	double r = dot(p - s.p1, base) / base.norm();
	return s.p1 + base * r;
}
Point reflect(Segment s, Point p) {
	return p + (project(s, p) - p) * 2.0;
}
int ccw(Point &p0, Point p1, Point p2) {
	Vector a = p1 - p0;
	Vector b = p2 - p0;
	if(cross(a, b) > EPS) return COUNTER_CLOCKWISE;
	if(cross(a, b) < -EPS) return CLOCKWISE;
	if(dot(a, b) < -EPS) return ONLINE_BACK;
	if(a.norm() < b.norm()) return ONLINE_FRONT;
	return ON_SEGMENT;
}
inline void ccw_print(int c) {
	if(c == COUNTER_CLOCKWISE) P("COUNTER_CLOCKWISE");
	else if (c == CLOCKWISE) P("CLOCKWISE");
	else if (c == ONLINE_BACK) P("ONLINE_BACK");
	else if (c == ONLINE_FRONT) P("ONLINE_FRONT");
	else P("ON_SEGMENT");
}
bool intersect(Point &p1, Point &p2, Point &p3, Point &p4) {
	return (ccw(p1, p2, p3) * ccw(p1, p2, p4) <= 0 &&
			ccw(p3, p4, p1) * ccw(p3, p4, p2) <= 0);
}
bool intersect(Segment &s1, Segment &s2) {
	return intersect(s1.p1, s1.p2, s2.p1, s2.p2);
}
double getDistance(Point &a, Point &b) { return (a - b).abs(); }
double getDistanceLP(Line &l, Point &p) {
	return abs(cross(l.p2 - l.p1, p - l.p1) / (l.p2 - l.p1).abs());
}
double getDistanceSP(Segment &s, Point &p) {
	if(dot(s.p2 - s.p1, p - s.p1) < 0.0) return (p - s.p1).abs();
	if(dot(s.p1 - s.p2, p - s.p2) < 0.0) return (p - s.p2).abs();
	return getDistanceLP(s, p);
}
double getDistance(Segment &s1, Segment &s2) {
	if(intersect(s1, s2)) return 0.0;
	return min(min(getDistanceSP(s1, s2.p1), getDistanceSP(s1, s2.p2)),
				min(getDistanceSP(s2, s1.p1), getDistanceSP(s2, s1.p2)));
}
int main() {
	cout << setprecision(10) << fixed;
	Point p0, p1, p2; cin >> p0.x >> p0.y >> p1.x >> p1.y;
	int q; cin >> q;
	rep(i, 0, q) {
		cin >> p2.x >> p2.y;
		ccw_print(ccw(p0, p1, p2));
	}
	return 0;
}

#include <iostream>
