int main()
{
	double p1x, p1y, p2x, p2y, p0x, p0y;
	int q;

	scanf_s("%lf %lf %lf %lf", &p0x, &p0y, &p1x, &p1y);

	pair<double, double> v01 = { p1x - p0x, p1y - p0y };


	scanf_s("%d", &q);

	for (int i = 0; i < q; i++)
	{
		scanf_s("%lf %lf", &p2x, &p2y);

		if ((p2x == p0x && p2y == p0y) || (p2x == p1x && p2y == p0y)){
			printf("ONLINE_SEGMENT\n");

			continue;
		}

		pair<double, double> v02 = { p2x - p0x, p2y - p0x };

		if (v01.first*v02.second == v01.second*v02.first)
		{
			if (v01.first*v02.first < 0)
			{
				printf("ONLINE_BACK\n");
				continue;
			}
			else
			{
				abs(v01.first) < abs(v02.first) ? printf("ONLINE_FRONT\n") : printf("ONLINE_SEGMENT\n");
				continue;
			}
		}

		double y01 = (v01.second / v01.first)*(p2x - p0x) + p0y;
		y01 < p2y ? printf("COUNTER_CLOCKWISE\n") : printf("CLOCKWISE\n");
	}
}

