            else if(getlen(v1)>=getlen(v2)){
                cout<<"ON_SEGMENT"<<endl;
            }
            else cout<<"ONLINE_FRONT"<<endl;
        }
        else if(temp<0){
            cout<<"CLOCKWISE"<<endl;
        }
        else cout<<"COUNTER_CLOCKWISE"<<endl;
    }
    return 0;
}

