    cin >> A >> B;
    int q;
    cin >> q;
    for (int i = 0; i < q; i++)
    {
        Point X;
        cin >> X;
        int ccw = Counter_Clockwise(A, B, X);
        if (ccw == 1)
            cout << "COUNTER_CLOCKWISE\n";
        else if (ccw == -1)
            cout << "CLOCKWISE\n";
        else if (ccw == 2)
            cout << "ONLINE_BACK\n";
        else if (ccw == -2)
            cout << "ONLINE_FRONT\n";
        else
            cout << "ON_SEGMENT\n";
    }
}

