        if(res==1) ans = "COUNTER_CLOCKWISE";
        if(res==-1) ans = "CLOCKWISE";
        if(res==2) ans = "ONLINE_BACK";
        if(res==-2) ans = "ONLINE_FRONT";
        if(res==3) ans = "ON_SEGMENT";

        cout << ans << endl;
    }


    return 0;
}

