    rep(i, q) {
        Point p2 = ps[i];
        Vector a = p1 - p0;
        Vector b = p2 - p0;
        string ans = "";
        if (cross(a, b) > EPS) ans = "COUNTER_CLOCKWISE";
        else if (cross(a, b) < -EPS) ans = "CLOCKWISE";
        else if (dot(a, b) < -EPS) ans = "ONLINE_BACK";
        else if (a.norm() < b.norm()) ans = "ONLINE_FRONT";
        else ans = "ON_SEGMENT";
        cout << ans << endl;
    }
}

