const double EPS = 1e-8;

int dcmp(double d) {
    if (fabs(d) < EPS) {
        return 0;
    }
    return d > 0? 1 : -1;
}

struct Vector {
    double x, y;
    Vector(double x, double y): x(x), y(y) {
    }
};

typedef Vector Point;

double dot(const Vector& a, const Vector& b) {
    return a.x * b.x + a.y * b.y;
}

double cross(const Vector& a, const Vector& b) {
    return a.x * b.y - a.y * b.x;
}

Vector operator-(const Point& a, const Point& b) {
    return Vector(a.x - b.x, a.y - b.y);
}

int main() {
    double x0, y0, x1, y1;
    cin >> x0 >> y0 >> x1 >> y1;
    Point p0(x0, y0);
    Point p1(x1, y1);

    int q;
    cin >> q;
    for (int i = 0; i < q; i++) {
        double x2, y2;
        cin >> x2 >> y2;
        Point p2(x2, y2);

        Vector v01 = p1 - p0;
        Vector v02 = p2 - p0;

        double d = dot(v01, v02);
        double c = cross(v01, v02);

        double norm1 = dot(v01, v01);
        double norm2 = dot(v02, v02);

        if (dcmp(c) > 0) {
            cout << "COUNTER_CLOCKWISE" << endl; 
        }
        else if (dcmp(c) < 0) {
            cout << "CLOCKWISE" << endl;
        }
        else if (dcmp(c) == 0 && dcmp(d) < 0 ) {
            cout << "ONLINE_BACK" << endl;
        }
        else if (dcmp(c) == 0 && dcmp(d) > 0 && dcmp(norm2 - norm1) > 0) {
            cout << "ONLINE_FRONT" << endl;
        }
        else {
            cout << "ON_SEGMENT" << endl;
        }

    }


    return 0;
}

