// 0: counter-clockwise
// 1: clockwise
// 2: online_back
// 3: online_front
// 4: on_segment
int ccw(P p0, P p1, P p2) {
    if (cross(p1 - p0, p2 - p0) > 0) return 0;
    if (cross(p1 - p0, p2 - p0) < 0) return 1;
    if (abs(p1 - p2) > abs(p1 - p0)) return 2;
    if (abs(p2 - p0) > abs(p1 - p0)) return 3;
    return 4;
}

