class Vector
{
public:
  double x;
  double y;

  Vector(){}
  Vector( double x, double y ) : x(x), y(y) {}

  Vector operator + ( const Vector v ) { return Vector( x + v.x, y + v.y ); }
  Vector operator - ( const Vector v ) { return Vector( x - v.x, y - v.y ); }
  Vector operator * ( const double k ) { return Vector( x * k, y * k ); }
  Vector operator / ( const double k ) { return Vector( x / k, y / k ); }
  bool operator < ( const Vector &v ) const { return x * x + y * y < v.x * v.x + v.y * v.y; }

  double abs() { return sqrt( norm() ); }
  double norm() { return x * x + y * y; }

  static double dot( Vector v1, Vector v2 ) { return v1.x * v2.x + v1.y * v2.y; }
  static double cross( Vector v1, Vector v2 ) { return v1.x * v2.y - v1.y * v2.x; }
};

class CounterClockwise
{
public:
  Vector p0, p1;
  CounterClockwise(){}
  CounterClockwise( double x0, double y0, double x1, double y1 ) : p0(x0, y0), p1(x1, y1) {}
  int solve( double x, double y );
};

int CounterClockwise::solve( double x, double y )
{
  Vector p10 = p1 - p0;
  Vector p20 = Vector( x, y ) - p0;

  double c = Vector::cross( p10, p20 );
  double d = Vector::dot( p10, p20 );

  if ( fabs( c ) < eps )
    {
      if ( d < 0 )
        cout << "ONLINE_BACK" << endl;
      else if ( p10.norm() < p20.norm() )
        cout << "ONLINE_FRONT" << endl;
      else
        cout << "ON_SEGMENT" << endl;
    }
  else if ( c > 0 )
    cout << "COUNTER_CLOCKWISE" << endl;
  else
    cout << "CLOCKWISE" << endl;

  return 0;
}

