double dot(Pint p1,Pint p2){
    return p1.first*p2.first + p1.second*p2.second;
}

double closs(Pint p1,Pint p2){
    return p1.first*p2.second - p1.second*p2.first;
}
double vector_length(Pint p){
    return pow(p.first*p.first+p.second*p.second,0.5);
}

double cos_(Pint p1,Pint p2){
    return dot(p1,p2)/(vector_length(p1)*vector_length(p2));
}

double sin_(Pint p1,Pint p2){
    return closs(p1,p2)/(vector_length(p1)*vector_length(p2));
}

Pdouble proj(Pint p1,Pint p2,Pint a,Pint b){
    Pint pp = Pint(p2.first-p1.first,p2.second-p1.second);
    double ans_x = (double)p1.first + vector_length(a)*cos_(a,b)*pp.first/vector_length(pp);
    double ans_y = (double)p1.second + vector_length(a)*cos_(a,b)*pp.second/vector_length(pp);

    return Pdouble(ans_x,ans_y);
}

bool vector_equal(Pint p,Pint q){
    return (p.first == q.first) && (p.second == q.second);
}

void clock_wise(Pint q0,Pint q1,Pint q2){
    Pint q0q1 = Pint(q1.first-q0.first,q1.second-q0.second);
    Pint q0q2 = Pint(q2.first-q0.first,q2.second-q0.second);

