        //cout << "alkhfl" << endl;
        if(vector_length(q0q1)<vector_length(q0q2)) cout << "ONLINE_FRONT" << endl;
        else cout << "ON_SEGMENT" << endl;
    }else{
        if(sin_(q0q1,q0q2)>0) cout << "COUNTER_CLOCKWISE" << endl;
        else if(sin_(q0q1,q0q2)<0) cout << "CLOCKWISE" << endl;
        else{
            if(cos_(q0q1,q0q2)<0) cout << "ONLINE_BACK" << endl;
            else if(vector_length(q0q1)<vector_length(q0q2)) cout << "ONLINE_FRONT" << endl;
            else cout << "ON_SEGMENT" << endl;
        }
    }
}

