struct Point{
	double x, y;
	void read(){scanf("%lf%lf", &x, &y);}
	Point():x(0.0), y(0.0){}
	Point(double a, double b = 0.0): x(a), y(b){}
	Point(const Point& p): x(p.x), y(p.y){}
	Point operator + (const Point& a){return {x + a.x, y + a.y};}
	Point operator - (const Point& a){return {x - a.x, y - a.y};}
	double operator * (const Point& a){return x * a.x + y * a.y;}
	double operator ^ (const Point& a){return x * a.y - y * a.x;}
	bool operator == (const Point& p){return sgn(x - p.x) == 0 && sgn(y - p.y) == 0;}
	bool operator < (const Point &p) const{
		if(sgn(x - p.x) == 0) 
			return sgn(y - p.y) == -1;
		return sgn(x - p.x) == -1;
	}
	double length(){return sqrt(x * x + y * y);}
}typedef Vector;
struct Line{
	Point a, b;
	void read(){a.read(); b.read();}
	Line() : a(0.0, 0.0) , b(0.0, 0.0){}
};
struct Segment{
	Point a, b;
	void read(){a.read(); b.read();}
	Segment() : a(0.0, 0.0) , b(0.0, 0.0){}
};
double distance(const Point &a, const Point &b) 
{
	double x = a.x - b.x;
	double y = a.y - b.y;
	return sqrt(x * x + y * y);
}
Point Projection(Point p, Line l)
{ 
	// 点在直线上的投影点
	Vector v1 = l.b - l.a;
	Vector v2 = p - l.a;
	double k1 = v1 * v2;
	double k2 = v1.length();
	k1 /= k2 * k2;
	return {l.a.x + v1.x * k1, l.a.y+ v1.y * k1};
}
Point Reflection(Point p, Line l)
{
	// 点关于直线对称
	Point p0 = Projection(p, l);
	Vector v0 = p0 - p;
	return p0 + v0;
}
int ccw(Point p, Segment sg)
{
	// p 关于(sg.a -> sg.b)的关系
	Vector v0 = sg.b - sg.a;
	Vector v1 = p - sg.a;

	int fcross = sgn(v0 ^ v1);

	if(fcross == 1) return 1; // 顺时针方向
	if(fcross == -1) return 2; // 逆时针方向

	int fdot = sgn(v0 * v1);

	if(fdot == -1) return 3; // 反向线段外共线
	if(v0.length() < v1.length()) return 4; //正向线段外共线
	return 5; //点在线段上
}
int main()
{
	Segment sg;
	sg.read();
	int n;
	scanf("%d", &n);
	while(n --)
	{
		Point p;
		p.read();
		int ans = ccw(p, sg);
		if(ans == 1) puts("COUNTER_CLOCKWISE");
		else if(ans == 2) puts("CLOCKWISE");
		else if(ans == 3) puts("ONLINE_BACK");
		else if(ans == 4) puts("ONLINE_FRONT");
		else puts("ON_SEGMENT");
	}
	return 0;
}

