};

int isp(const point &a, const point &b, const point &c) {
    point p = a - b, q = c - a;
    if (sgn(p.cross(q)) < 0) return 1;
    if (sgn(p.cross(q)) > 0) return -1;
    p = b - a; q = c - a;
    if (sgn(p.dot(q)) < 0) return -2;
    p = a - b; q = c - b;
    if (sgn(p.dot(q)) < 0) return 2;
    return 0;
}


