using Real = long double;

const Real EPS = 1e-10;

// 宇宙船演算子
inline Real compare(Real a, Real b) {
    if (std::abs(a - b) < EPS) return 0;
    return a - b > 0 ? 1 : -1;
}

template <class T>
inline T sq(T x) { return x * x; }


using Point = std::complex<Real>;
using Segment = std::pair<Point, Point>;

inline Real length(Segment l) {
    return std::abs(l.second - l.first);
}

// inner product
inline Real dot(Point x, Point y) {
    return std::real(std::conj(x) * (y));
}

// outer product
inline Real cross(Point x, Point y) {
    return std::imag(std::conj(x) * (y));
}

// lに対するxの正射影
Point proj(Segment l, Point p) {
    Real ratio = dot(l.second - l.first, p - l.first) / sq(length(l));
    return l.first + (l.second - l.first) * ratio;
}

// lを軸としてxに対称な点
Point refl(Segment l, Point p) {
    Point t = proj(l, p);
    return t + (t - p);
}

// lに対するpの位置
// counter clockwise -> 1, clockwise   -> -1
// online front      -> 2, online back -> -2
// on segment        -> 0
int side(Segment l, Point p) {
    Real c = cross(l.second - l.first, p - l.first);
    if (compare(c, 0) != 0) return compare(c, 0);

    Real d = dot(l.second - l.first, p - l.first);
    if (compare(d, 0) < 0) return -2;

    return (compare(length(Segment(l.first, p)), length(l)) > 0 ? 2 : 0);
}


