{
    cin.tie(0);
    ios::sync_with_stdio(false);
    int a,b,c,d; cin>>a>>b>>c>>d;
    V A(a,b), B(c,d);
    int q; cin>>q;
    cout<<setp(12);
    while(q--){
        int x,y; cin>>x>>y;
        V C = V(x,y);
        int c = ccw(A,B,C);
        if (c==1)  cout<<"COUNTER_CLOCKWISE\n";
        if (c==-1) cout<<"CLOCKWISE\n";
        if (c==2)  cout<<"ONLINE_BACK\n";
        if (c==-2) cout<<"ONLINE_FRONT\n";
        if (c==0)  cout<<"ON_SEGMENT\n";
    }
    return 0;
}

