using namespace std;

#define EPS 1e-14

#define PI 3.1415926535897932385

double dist(int x, int y)
{
	return sqrt(x * x + y * y);
}

double angle(int x, int y)
{
	return atan2((double)(y), (double)(x));
}

int main()
{
	int x0, y0, x1, y1, x2, y2, q;

	cin >> x0 >> y0 >> x1 >> y1 >> q;

	for (int i = 0; i < q; i++)
	{
		cin >> x2 >> y2;

		int ax = x1 - x0;
		int ay = y1 - y0;
		int bx = x2 - x0;
		int by = y2 - y0;

		double ad = dist(ax, ay);
		double bd = dist(bx, by);

		double at = angle(ax, ay);
		double bt = angle(bx, by);

		double st = bt - at;

		if (st < -PI - EPS)
		{
			st += 2 * PI;
		}

		if (st > PI + EPS)
		{
			st -= 2 * PI;
		}

		if (bx == 0 && by == 0)
		{
			cout << "ON_SEGMENT" << endl;
		}
		else if ((double)by / (double)bx == (double)ay / (double)ax)
		{
			if (ax * bx < 0 || ay * by < 0)
			{
				cout << "ONLINE_BACK" << endl;
			}
			else
			{
				if (ad < bd)
				{
					cout << "ONLINE_FRONT" << endl;
				}
				else
				{
					cout << "ON_SEGMENT" << endl;
				}
			}
		}
		else if (st > EPS)
		{
			cout << "COUNTER_CLOCKWISE" << endl;
		}
		else
		{
			cout << "CLOCKWISE" << endl;
		}
	}

	return 0;
}

