class VectorOperation {//操作符不必继承
public:
	double dot(Point a, Point b)//点乘
	{
		return a.x * b.x + a.y * b.y;
	}
	double cross(Point a, Point b)//叉乘
	{
		return a.x * b.y - a.y * b.x;
	}
	double abs(Point s)
	{
		return sqrt(s.norm());
	}
	double getDistance(Point a, Point b)
	{
		return abs(a - b);
	}
	int rotation(Point p0, Point p1, Point p2)
	{
#define COUNTER_CLOCKWISE -1
#define CLOCKWISE 1
#define ONLINE_BACK -2
#define ONLINE_FRONT 2
#define ON_SEGMENT 0
		Vector a = p1 - p0;Vector b = p2 - p0;
		double Cross = cross(a, b);
		if (Cross > 0.0)return COUNTER_CLOCKWISE;
		if (Cross < 0.0)return CLOCKWISE;
		if (dot(a, b) < 0.0)return ONLINE_BACK;
		if (a.norm() < b.norm())return ONLINE_FRONT;

		return ON_SEGMENT;
#undef COUNTER_CLOCKWISE -1
#undef CLOCKWISE 1
#undef ONLINE_BACK -2
#undef ONLINE_FRONT 2
#undef ON_SEGMENT 0
	}
};

