class Line2D{
    Point2D p1, p2;
public:
    Line2D(){}
    Line2D(Point2D a, Point2D b): p1(a), p2(b){}

    void setP1(const Point2D p){p1 = p;}
    Point2D getP1(){return p1;}
    void setP2(const Point2D p){p2 = p;}
    Point2D getP2(){return p2;}

    friend Point2D project(Line2D l, Point2D p){
        Point2D base = l.p2 - l.p1;
        double r = dot(p - l.p1, base) / norm(base);
        return l.p1 + base * r;
    }

    friend Point2D reflect(Line2D l, Point2D p){
        return p + 2.0 * (project(l, p) - p);
    }
};

int ccwSearch(Point2D a, Point2D b){
    if(cross(a, b) > EPS) return 0;//CCW
    if(cross(a, b) < -EPS) return 1;//CW
    if(dot(a, b) < -EPS) return 2;//OLB
    if(norm(a) < norm(b)) return 3;//OLF
    return 4;//OS
}


