pdd get_incline(pdd p1, pdd p2, double* len) {
    pdd inc = make_pair(
        p2._x - p1._x,
        p2._y - p1._y
    );
    double leng = norm(inc);
    *len = leng;
    return make_pair(
        inc._x / leng,
        inc._y / leng
    );
}

bool is_equal(pdd p1, pdd p2) {
    return abs(p1._x - p2._x) < EPS && abs(p1._y - p2._y) < EPS;
}

