namespace asl
{

    template <typename numeric_type>
    class point
    {
    public:
        numeric_type x, y;

        point(numeric_type x = 0, numeric_type y = 0) : x(x), y(y) {}

        point<numeric_type> &operator-=(const point<numeric_type> &ri)
        {
            x -= ri.x;
            y -= ri.y;
            return *this;
        }

    };

    template <typename numeric_type>
    std::istream &operator>>(std::istream &is, point<numeric_type> &p)
    {
        return is >> p.x >> p.y;
    }

    template <typename numeric_type>
    class line
    {
    public:
        point<numeric_type> p, q;

    };

    template <typename numeric_type>
    std::istream &operator>>(std::istream &is, line<numeric_type> &l)
    {
        return is >> l.p >> l.q;
    }

    template <typename numeric_type>
    class segment : public line<numeric_type>
    {
    };

    template <typename numeric_type>
    numeric_type cross(point<numeric_type> a, point<numeric_type> b)
    {
        return a.x * b.y - a.y * b.x;
    }

    template <typename numeric_type>
    numeric_type dot(point<numeric_type> a, point<numeric_type> b)
    {
        return a.x * b.x + a.y * b.y;
    }

    enum PointOrder
    {
        CounterClockwise,
        Clockwise,
        AlignedFirst,
        AlignedLast,
        AlignedIn, 
    };

    template <typename numeric_type>
    PointOrder ccw(point<numeric_type> a, point<numeric_type> b, point<numeric_type> c)
    {
        b -= a;
        c -= a;
        if (cross(b, c) > 0)
            return PointOrder::CounterClockwise;
        else if (cross(b, c) < 0)
            return PointOrder::Clockwise;
        else if (dot(b, c) < 0)
            return PointOrder::AlignedFirst; 
        else if (dot(b, b) < dot(c, c))
            return PointOrder::AlignedLast; 
        else
            return PointOrder::AlignedIn; 
    }

} 

