// 0: counter-clockwise
// 1: clockwise
// 4: on_segment
// 2: online_back
// 3: online_front
int ccw(P p0, P p1, P p2) {
    if (cross(p1 - p0, p2 - p0) > 0) return 0;
    if (cross(p1 - p0, p2 - p0) < 0) return 1;
    double d01 = abs(p0 - p1);
    double d12 = abs(p1 - p2);
    double d20 = abs(p2 - p0);
    if (d12 + d20 == d01) return 4;
    if (d01 + d20 == d12) return 2;
    return 3;
}

