int main(){
	double x1,x2,xp,y1,y2,yp1,yp,naiseki,norm1,norm2,gaiseki;
	int q;

	scanf("%lf %lf %lf %lf",&x1,&y1,&x2,&y2);
	scanf("%d",&q);

	for(int i=0; i < q; i++){
		scanf("%lf %lf",&xp,&yp);
		norm1 = sqrt((x2-x1)*(x2-x1)+(y2-y1)*(y2-y1));
		norm2 = sqrt((xp-x1)*(xp-x1)+(yp-y1)*(yp-y1));
		naiseki = (xp-x1)*(x2-x1)+(yp-y1)*(y2-y1);
		gaiseki = (x2-x1)*(yp-y1)-(xp-x1)*(y2-y1);
		if(fabs(naiseki/(norm1*norm2)+1) < 0.00000001){
			printf("ONLINE_BACK\n");
		}else if(fabs(naiseki-norm1*norm2)< 0.00000001){
			if(norm1 >= norm2){
				printf("ON_SEGMENT\n");
			}else{
				printf("ONLINE_FRONT\n");
			}
		}else if(gaiseki > 0){
			printf("COUNTER_CLOCKWISE\n");
		}else{
			printf("CLOCKWISE\n");
		}
	}

    return 0;
}

