struct vecter {
	double x, y, r, norm;
	vecter() {}
	vecter(double x_, double y_) :x(x_), y(y_) {
		norm = x * x + y * y;
		r = sqrt(x*x + y * y);
	}
	vecter operator+(vecter v) {
		return vecter(x + v.x, y + v.y);
	}
	vecter operator-(vecter v) {
		return vecter(x - v.x, y - v.y);
	}
	vecter operator/(double k) {
		return vecter(x/k, y/k);
	}
	vecter operator*(double k) {
		return vecter(x*k,y*k);
	}
	double operator*(vecter v) { //内積
		return x * v.x + y * v.y;
	}
	double angle() { //vecter(1,0)となす角(Radians)
		if (r == 0)return 0;
		else	return acos(x / r)*(y >= 0 ? 1 : -1);
	}
	double angle(vecter v) { //なす角(Radians)
		if((r * v.r)>0)
		return acos(*this*v / (r * v.r));
		else return 0;
	}
	vecter unit() {
		return (r == 0 ? vecter() : vecter(x, y) / r);
	}
};
int main() {
	double x, y;
	cin >> x >> y;
	vecter p1(x, y);
	cin >> x >> y;
	vecter p2(x, y);
	cin >> n;
	rep(i, n) {
		cin >> x >> y;
		vecter P(x, y);
		vecter q1 = p2 - p1, q2 = P - p1;
		if (q1.angle() == q2.angle()) {
			if (q1.norm < q2.norm) {
				cout << "ONLINE_FRONT" << endl;
			}
			else {
				cout << "ON_SEGMENT" << endl;
			}
		}
		else {
			if (q1.angle() - q2.angle()<PI/2) {
				cout << "COUNTER_CLOCKWISE" << endl;
			}
			else if (q1.angle() - q2.angle()==PI / 2) {
				cout << "ONLINE_BACK" << endl;
			}
			else {
				cout << "CLOCKWISE" << endl;
			}
		}
	}
	return 0;
}

