//点のクラス
class Point{
    public:
        double x,y;
        Point(double x=0,double y=0):x(x),y(y){}

        Point operator + (Point p){return Point(x+p.x,y+p.y);}
        Point operator - (Point p){return Point(x-p.x,y-p.y);}
        Point operator * (double a){return Point(a*x,a*y);}
        Point operator / (double a){return Point(x/a,y/a);}

        double norm(){return x*x+y*y;}
        double abs(){return sqrt(norm());}    

        bool operator < (const Point &p)const{
            return x!=p.x?x<p.x:y<p.y;
        }
        bool operator == (const Point &p)const{
            return equals(x,p.x)&&equals(y,p.y);
        }
};
typedef Point Vector;
//内積
double dot(Vector a,Vector b){ return a.x*b.x+a.y*b.y; }
//外戚
double cross(Vector a,Vector b){ return a.x*b.y-a.y*b.x; }
//ベクトルa,bの位置関係
int ccw(Point p0,Point p1,Point p2){
	Vector a=p1-p0;
	Vector b=p2-p0;
	if(cross(a,b)>EPS) return 1;//p0,p1,p2が反時計回り
	if(cross(a,b)<-EPS) return -1;//p0,p1,p2が時計回り
	if(dot(a,b)<-EPS) return 2;//p1,p0,p2の順で一直線
	if(a.norm()<b.norm()) return -2;//p0,p1,p2の順で一直線
	return 0; //p0,p2,p1の順で一直線
}
int main(){
    int q,flag;
    Point p1,p2;
    scanf("%lf %lf %lf %lf",&p1.x,&p1.y,&p2.x,&p2.y);
    cin>>q;
    Point p;
    for(int i=0;i<q;i++){
        cin>>p.x>>p.y;
        flag=ccw(p1,p2,p);
        switch (flag){
            case 1:
                cout<<"COUNTER_CLOCKWISE";
                break;
            case -1:
                cout<<"CLOCKWISE";
                break;
            case 2:
                cout<<"ONLINE_BACK";
                break;
            case -2:
                cout<<"ONLINE_FRONT";
                break;
            default:    
                cout<<"ON_SEGMENT";

        }
        cout<<"\n";
    }
    return 0;
}

#include<bits/stdc++.h>
using namespace std;
const double eps = 1e-10;
struct Node
{
    double x, y;
    Node(double xx = 0, double yy = 0){
        x = xx; y = yy;
    }

    Node operator + (Node b){
        return Node(x+b.x, y+b.y);
    }
    Node operator - (Node b){
        return Node(x-b.x, y-b.y);
    }
    Node operator * (double a){
        return Node(a*x, a*y);
    }
    Node operator / (double a){
        return Node(x/a, y/a);
    }

    double norm(){return x*x+y*y;}
    double len(){
        return sqrt(norm());
    }

    bool operator < (const Node &p) const{
        return x!=p.x ? x<p.x : y<p.y;
    }

    bool operator == (const Node &p) const {
        return fabs(x-p.x)<eps && fabs(y-p.y)<eps;
    }
};

typedef Node vtr;

struct line{
    Node a, b;
    line(Node x, Node y){
        a = x, b = y;
    }
};

//向量点积
double dot(vtr a, vtr b){
    return a.x*b.x+a.y*b.y;
}

//向量叉积
double cross(vtr a, vtr b){
    return a.x*b.y-a.y*b.x;
}

//投影 求垂足坐标
//点p在直线p1p2上的投影
Node project(line s, Node p)
{
    vtr base = s.b-s.a;
    double r = dot(p-s.a, base)/base.norm();
    return s.a+base*r;
}

//映象 求p以s为对称轴的对称点
Node reflect(line s, Node p){
    return p+(project(s, p)-p)*2.0;
}

int ccw(Node p0, Node p1, Node p2)
{
    vtr a = p1-p0;
    vtr b = p2-p0;
    if(cross(a, b) > eps) return 1;
    if(cross(a, b) < -eps) return -1;
    if(dot(a, b) < -eps) return 2;
    if(a.norm() < b.norm()) return -2;
    return 0;
}

int main()
{
    Node p0, p1, p2;
    cin>>p0.x>>p0.y>>p1.x>>p1.y;
    int q; scanf("%d", &q);
    while(q--)
    {
        cin >> p2.x >> p2.y;
        switch(ccw(p0, p1, p2))
        {
            case 1:cout<<"COUNTER_CLOCKWISE"<<endl; break;
			case -1: cout<<"CLOCKWISE"<<endl; break;
			case 2: cout<<"ONLINE_BACK"<<endl; break;
			case -2: cout<<"ONLINE_FRONT"<<endl; break;
			case 0: cout<<"ON_SEGMENT"<<endl; break;
        }
    }
    return 0;
}

